import React, {Component} from 'react';
import api from "../../services/api";
import { Link } from 'react-router-dom';

import './styles.css'

export default class Main extends Component {

    // variavel monitorada e cada alteração de valor chama o render
    state = {
        products: [],
        productInfo: {},
        page: 1,
    };
    // executado assim que o componente e montado na tela
    componentDidMount(){
        this.loadProducts();

    }

    //as funcoes proprias, criar no modelo arrow function
    //no outro modelo nao consegue enxergar o this
    loadProducts = async (page = 1) => {
        const response = await api.get(`/products?page=${page}`);
        
        const {docs, ...productInfo} = response.data;

        //console.log(response);
        this.setState( { products: docs, productInfo, page });
    };
    prevPage = () => {
        const {page, productInfo} = this.state;

        if(page === 1) return;
        
        const pageNumber = page - 1;
        this.loadProducts(pageNumber);
    }
    nextPage = () => {
        const {page, productInfo} = this.state;
        
        if(page === productInfo.pages) return;
        
        const pageNumber = page + 1;
        this.loadProducts(pageNumber);
    }

    render(){
        const {products, page, productInfo} = this.state;

        return (
            <div className="product-list">
                {products.map( product => (
                    <article key={product._id}>
                        <strong>{product.title}</strong>
                        <p>{product.description}</p>
                        <Link to={`/products/${product._id}`}>Acessar</Link> 
                    </article>
                ))}
                <div className="actions">
                    <button disabled={page === 1} onClick={this.prevPage}>Anterior</button>
                    <button disabled={page === productInfo.pages} onClick={this.nextPage}>Proxima</button>
                </div>
            </div>

        );
    }
}
